    def test_rlp_write(self):
        for i, o in self.vectors:
            o = unhexlify(o)
            w = bytearray()
            rlp.write(w, i)
            self.assertEqual(w, o)


    def test_rlp_length(self):
        for i, o in self.vectors:
            length = rlp.length(i)
            self.assertEqual(length, len(o) // 2)



